import React from 'react'
import JobArticle from '../JobArticle/JobArticle'
import styles from './JobListContainer.module.scss'
import AppContext from '../../context/AppContext'

const JobListContainer = ({ data }) => {
   const filteredJobs = data.filter((job) => {
      // Sprawdź, czy każda nazwa w tablicy filters znajduje się w właściwościach 'tools', 'language', 'role' lub 'level' elementu
      return ctx.filters.every((filter) => {
         return job.tools.includes(filter) || job.languages.includes(filter) || job.role === filter || job.level === filter
      })
   })
   const jobList = data.map((job) => <JobArticle key={job.id} data={job} />)
   return <main className={styles['job-list']}>{jobList}</main>
}

export default JobListContainer
