import React, { useContext, useState } from 'react'
import JobArticle from '../JobArticle/JobArticle'
import styles from './JobListContainer.module.scss'
import AppContext from '../../context/AppContext'

const JobListContainer = ({ data }) => {
   const [buttons, setButtons] = useState([])

   const setButtonsHandler = (data) => {
      if (!buttons.includes(data)) {
         setButtons([...buttons, filter])
      }
   }
   const ctx = useState(AppContext)
   const filteredElements = data.elements.filter((element) => {
      return element.buttons.includes(ctx.filters)
   })
   const jobList = filteredElements.map((job) => <JobArticle key={job.id} data={job} onSetButtons={setButtonsHandler} />)
   return <main className={styles['job-list']}>{jobList}</main>
}

export default JobListContainer
