import React, { useState } from 'react'
import JobArticle from '../JobArticle/JobArticle'
import styles from './JobListContainer.module.scss'

const JobListContainer = ({ data }) => {
   const [filterData, setFilterData] = useState([])
   const filterDataHandler = (data) => {
         if (filterData.includes(data)) {
         setFilterData(filterData.filter((name) => name !== data))
      } else {
         setFilterData([...filterData, e.target.textContent])
      }
   }
   const jobList = data.map((job) => <JobArticle key={job.id} data={job} onFilterData={filterDataHandler} />)
   return <main className={styles['job-list']}>{jobList}</main>
}

export default JobListContainer
